import React from 'react';
import Button from "react-bootstrap/Button";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import NavbarBrand from "react-bootstrap/NavbarBrand";
import Accordion from "react-bootstrap/Accordion";
import Modal from "react-bootstrap/Modal";
import Card from "react-bootstrap/Card";
import AccordionToggle from 'react-bootstrap/AccordionToggle';
import Table from 'react-bootstrap/Table';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSignOutAlt } from '@fortawesome/free-solid-svg-icons'
import { faBoxOpen } from '@fortawesome/free-solid-svg-icons'
import { faCoins } from '@fortawesome/free-solid-svg-icons'

import styled from "styled-components";

import logo from "../images/pokepacksonline.png"

const fetch = require("node-fetch");
const checkForLocalToken = require("../utils/checkForLocalToken");

const Styles = styled.div`

`

const Img = styled.img`
    height: auto;
    width: 30%;
`

export default class ShopItemListComponent extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            itemsComponents: null,
            informationMessage: "null",
        }
    }

    componentDidMount(){

    }

    renderItem = () => {
        // return (
        //     <h1>test</h1>
        // );
    }

    render() {

        const Styles = styled.div`
            .nav-link{
                color: #FFFFFF !important;
            }

            .navbar{
                background-color: #272727 !important;
            }
        `
        
        return (
            <div>
                <Card>
                    <Card.Header>
                        <AccordionToggle as={Button} variant="link" eventKey="0">
                            {this.props.itemName}
                        </AccordionToggle>
                    </Card.Header>
                    <Accordion.Collapse eventKey="0">
                        <Card.Body>
                            <Table striped bordered hover>
                                <thead>
                                    <tr>
                                        <th>Item Name</th>
                                        <th>Description</th>
                                        <th>Cost</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.state.itemsComponents}
                                </tbody>
                            </Table>
                            <h1>{this.state.informationMessage}</h1>
                        </Card.Body>
                    </Accordion.Collapse>
                </Card>
            </div>
        );
    }
}
