import React from 'react';
import Button from "react-bootstrap/Button";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import NavbarBrand from "react-bootstrap/NavbarBrand";
import Accordion from "react-bootstrap/Accordion";
import Modal from "react-bootstrap/Modal";
import Card from "react-bootstrap/Card";
import AccordionToggle from 'react-bootstrap/AccordionToggle';
import Table from 'react-bootstrap/Table';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSignOutAlt } from '@fortawesome/free-solid-svg-icons'
import { faBoxOpen } from '@fortawesome/free-solid-svg-icons'
import { faCoins } from '@fortawesome/free-solid-svg-icons'

import styled from "styled-components";

import logo from "../images/pokepacksonline.png"

import CardListComponent from "./CardListComponent";

const fetch = require("node-fetch");
const checkForLocalToken = require("../utils/checkForLocalToken");



export default class PackListComponent extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            cost: 0,
            displayPulledCards: false,
            pulledCards: {},
          }
    }

    componentDidMount(){
        if (this.props.cost == null){
            this.setState({cost: 10});        
        } else {
            this.setState({cost: this.props.cost})
        }
    }

    renderCards(cards) {
        return cards.map(card => <CardListComponent key={card.id} code={card.id} name={card.name} rarity={card.rarity} />)
    }

    openPack = (event) => {
        event.preventDefault();
        checkForLocalToken.checkForLocalTokenNoUserObject()
        .then(returnObject => {
            if (returnObject != null){

                fetch("/api/packs/open/"+this.props.code, {method: "GET", headers:{"accepts": "application/json", "x-auth-token": returnObject.token}})
                .then(res => res.json())
                .then(json => {
                  
                    this.setState({
                        pulledCards: json,
                    })
                    this.showPulledCards();
                })

                
            }
        })
    }

    hidePulledCards = () =>{
        this.setState({displayPulledCards: false});
    }

    showPulledCards = () =>{
        this.setState({displayPulledCards: true});
    }

    // displayPulledCardsModal(){   
    //     if (this.state.displayPulledCards == true){
    //         return (
                
    //         );
    //     }
    // }

    render() {

        const Styles = styled.div`
            .nav-link{
                color: #FFFFFF !important;
            }

            .navbar{
                background-color: #272727 !important;
            }
        `

        const Logo = styled.img`
            height: auto;
            width: 15%;
        `

        //<Logo className="float-left" src={logo}>
        //<NavbarBrand style={{color: "#FFFFFF", fontSize: "2rem",}}></NavbarBrand>
        return (
            <div>
                <Modal show={this.state.displayPulledCards} onHide={this.hidePulledCards} size="lg" aria-labelledby="contained-modal-title-vcenter" centered>
                    <Modal.Header>
                        <Modal.Title id="contained-modal-title-vcenter">
                            Modal heading
                        </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <h1>Pokemon Cards Here</h1>
                    </Modal.Body>
                    <Modal.Footer>
                        <Button onClick={this.hidePulledCards}>Close</Button>
                    </Modal.Footer>
                </Modal>
                <Card>
                    <Card.Header>
                        <AccordionToggle as={Button} variant="link" eventKey={this.props.code}>
                            {this.props.name}  
                            <Logo style={{marginLeft: "0.5rem", marginRight: "0.5rem"}}src={require("../images/packs/" + this.props.code + "/" + this.props.code + "Logo" +  ".jpg")}/>
                        </AccordionToggle>
                        <br></br>
                        <Button variant="success" onClick={this.openPack}>Open Pack ${this.state.cost}</Button>
                    </Card.Header>
                    <Accordion.Collapse eventKey={this.props.code}>
                        <Card.Body>
                            <Table striped bordered hover>
                                <thead>
                                    <tr>
                                        <th>Card Code</th>
                                        <th>Name</th>
                                        <th>Rarity</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.renderCards(this.props.cards)} 
                                </tbody>
                            </Table>
                        </Card.Body>
                    </Accordion.Collapse>
                </Card>
            </div>
        );
    }
}
