import React from 'react';
import Button from "react-bootstrap/Button";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import NavbarBrand from "react-bootstrap/NavbarBrand";
import Accordion from "react-bootstrap/Accordion";
import Modal from "react-bootstrap/Modal";
import Card from "react-bootstrap/Card";
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import AccordionToggle from 'react-bootstrap/AccordionToggle';
import Table from 'react-bootstrap/Table';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSignOutAlt } from '@fortawesome/free-solid-svg-icons'
import { faBoxOpen } from '@fortawesome/free-solid-svg-icons'
import { faCoins } from '@fortawesome/free-solid-svg-icons'

import styled from "styled-components";

import logo from "../images/pokepacksonline.png"

import PackListCardComponent from "./PackListCardComponent";

const fetch = require("node-fetch");
const checkForLocalToken = require("../utils/checkForLocalToken");


const Styles = styled.div`
.nav-link{
    color: #FFFFFF !important;
}

.navbar{
    background-color: #272727 !important;
}
`

const Logo = styled.img`
height: auto;
width: 15%;
`

const Img = styled.img`
    height: auto;
    width: 30%;
`

export default class AddPackComponent extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            packCode: "",
            informationMessage: "",
            buttons: [],
          }
    }

    componentDidMount(){
    }

    handleInputChange = (event) =>{
        this.setState({
            packCode: event.target.value
        })
    }

    handleSubmit = (event) => {
        this.setState({
            informationMessage: "Processing...",
        })
        checkForLocalToken.checkForLocalTokenNoUserObject()
        .then(returnObject => {
          if (returnObject != null){
            this.setState({
              token: returnObject.token,
          })
          }
        })
        .then(() => {
            fetch("/api/packs/addpack/" + this.state.packCode, {method: "GET", headers:{"accepts": "application/json", "x-auth-token": this.state.token}})
            .then(res => res.json())
            .then(json => {
                this.setState({
                    informationMessage: json.msg
                })

                if (json.rarities){
                    let buttons = []
                    json.rarities.forEach(rarity => {
                        buttons.push(<button>{rarity}</button>)
                    })
                    this.setState({
                        buttons: buttons
                    })
                }
            });
        })
    }

    render() {
        return (
            <div>
                <Form className="custom-form">
                    <Form.Group controlId="packId">
                    <Form.Label style={{color: "white" ,width: "100%"}}>Pack ID</Form.Label>
                        <div className="d-flex align-items-center"> 
                            <Form.Control style={{marginRight: "0.5rem"}} type="string" placeholder="" onChange={this.handleInputChange}/>
                            <Button variant="primary" onClick={this.handleSubmit}>
                                Add
                            </Button>
                        </div>
                        <h2 style={{color :"white"}}>{this.state.informationMessage}</h2>
                    </Form.Group>
                </Form>
                {this.state.buttons}
            </div>
        );
    }
}
