import React from 'react';
import Button from "react-bootstrap/Button";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import NavbarBrand from "react-bootstrap/NavbarBrand";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSignOutAlt } from '@fortawesome/free-solid-svg-icons'
import { faBoxOpen } from '@fortawesome/free-solid-svg-icons'
import { faCoins } from '@fortawesome/free-solid-svg-icons'
import { faAddressCard } from '@fortawesome/free-solid-svg-icons'
import { faUserShield } from '@fortawesome/free-solid-svg-icons'
import { faWarehouse } from '@fortawesome/free-solid-svg-icons'
import { faPlusSquare } from '@fortawesome/free-solid-svg-icons'

import styled from "styled-components";

import logo from "../images/pokepacksonline.png"

const Styles = styled.div`

.nav-link{
    color: #FFFFFF !important;
}

.col{
    background-color: #272727;
    width: 100%;
    border-radius: 0;
}

.navbar{
    background-color: #272727 !important;
}
`

export default class NavbarComponent extends React.Component{
    constructor(props){
        super(props);

        this.state = {

          }
    }

    componentDidUpdate(){
        console.log("remount")
    }



    handleLogOut = (event) =>{
        event.preventDefault();
        let dataToParent = {
            logOut: true
        }
        this.props.callbackToParent(dataToParent);
    }

    handleInventory = (event) =>{
        event.preventDefault();
        let dataToParent = {
            inventory: true
        }
        this.props.callbackToParent(dataToParent);
    }

    handleOpenPacks = (event) =>{
        event.preventDefault();
        let dataToParent = {
            openPacks: true
        }
        this.props.callbackToParent(dataToParent);
    }

    handleAddPacks = (event) =>{
        event.preventDefault();
        let dataToParent = {
            addPack: true
        }
        this.props.callbackToParent(dataToParent);
    }

    render() {
        return (
            <Styles>
                <Row>
                    <Col>
                    <Navbar>
                        <Nav variant="pills" defaultActiveKey="PlayerStatsComponent" activeKey={this.props.leftPanelComponent} className="mr-auto">
                            <Nav.Link eventKey="PlayerStatsComponent"><FontAwesomeIcon icon={faAddressCard}/> Player Stats</Nav.Link>
                            <Nav.Link eventKey="null"><FontAwesomeIcon icon={faCoins}/> Shop</Nav.Link>
                            <Nav.Link eventKey="null" onClick={this.handleLogOut}><FontAwesomeIcon icon={faSignOutAlt}/> Log Out</Nav.Link>
                        </Nav>
                    </Navbar>
                    </Col>
                </Row>
            </Styles>
            
        );
    }
}
