import React from 'react';
import Card from "react-bootstrap/Card";
import Fade from "react-bootstrap/Fade";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSignOutAlt } from '@fortawesome/free-solid-svg-icons'
import ReactCSSTransitionGroup from 'react-addons-css-transition-group';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import styled from "styled-components";

import logo from "../images/placeholder.jpg"

const Styles = styled.div`
.col {
    background-color: #272727;
    width: 30rem;
    height: 100vh;
    border-radius: 0;
}

.custom-form {
    margin: 0 auto !important;
    width: 80% !important;
}

.form-label{
    color: white;
    float: left;
}


.custom-profile-image{
    width: 150px;
    height: 150px;
    border-radius: 50%;
}

.custom-button {
    margin: 0 auto;
}
`

const H4 = styled.h4`
color: #FFFFFF;
`

const H5 = styled.h5`
font-size: 1.2rem;
color: #FFFFFF;
`

export default class RegisterComponent extends React.Component{
    constructor(props, context) {
		super(props, context);

		this.state = {
            open: true,
        };
        
    }

    handleInputChange = event => {
		this.setState({
		    [event.target.id]: event.target.value
		});
    };
    
    handleSubmit = (event) => {
        event.preventDefault();
        let dataToParent ={
            register: true,
            email: this.state.email,
            username: this.state.username,
            password: this.state.password,
        }
        {this.props.callbackToParent(dataToParent)}
    }

    loginOnClick = () => {
        let dataToParent ={
            componentToLoad: "LoginComponent",
        }
        {this.props.callbackToParent(dataToParent)}
    }

    registerOnClick = () => {
        let dataToParent ={
            componentToLoad: "LoginComponent",
        }
        {this.props.callbackToParent(dataToParent)}
    }



    render() { 
        const { open } = true;

        

        return (
            <Styles key="stylesadada">
                <Col>
                    <Row>
                        <Form className="custom-form">
                            <Form.Group controlId="email">
                                <Form.Label>Email Address</Form.Label>
                                <Form.Control key="test" type="string" placeholder="Enter Email" onChange={this.handleInputChange}/>
                            </Form.Group>
                            <Form.Group controlId="username">
                                <Form.Label>Username</Form.Label>
                                <Form.Control type="string" placeholder="Enter Username" onChange={this.handleInputChange}/>
                            </Form.Group>
                            <Form.Group controlId="password">
                                <Form.Label>Password</Form.Label>
                                <Form.Control type="password" placeholder="Enter Password" onChange={this.handleInputChange}/>
                            </Form.Group>
                            <Form.Group controlId="confirmPassword">
                                <Form.Label>Confirm Password</Form.Label>
                                <Form.Control type="password" placeholder="Re-Enter Password" onChange={this.handleInputChange}/>
                            </Form.Group>
                            <Button variant="primary" type="submit" onClick={this.handleSubmit}>
                                Submit
                            </Button>
                        </Form>
                        <Button className="custom-button" variant="link" onClick={() => this.loginOnClick()}>Back To Login</Button>
                    </Row>
                </Col>
            </Styles>
        );
    }
}
