import React from 'react';
import Button from "react-bootstrap/Button";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import NavbarBrand from "react-bootstrap/NavbarBrand";
import Accordion from "react-bootstrap/Accordion";
import Modal from "react-bootstrap/Modal";
import Card from "react-bootstrap/Card";
import AccordionToggle from 'react-bootstrap/AccordionToggle';
import Table from 'react-bootstrap/Table';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSignOutAlt } from '@fortawesome/free-solid-svg-icons'
import { faBoxOpen } from '@fortawesome/free-solid-svg-icons'
import { faCoins } from '@fortawesome/free-solid-svg-icons'

import styled from "styled-components";

import logo from "../images/pokepacksonline.png"

import PackListCardComponent from "./PackListCardComponent";

const fetch = require("node-fetch");
const checkForLocalToken = require("../utils/checkForLocalToken");

const Styles = styled.div`

`

const Img = styled.img`
    height: auto;
    width: 30%;
`

export default class InventoryListComponent extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            inventory: null,
            cardsComponent: null,
          }
    }

    componentDidMount(){
        checkForLocalToken.checkForLocalTokenNoUserObject()
        .then(returnObject => {
          if (returnObject != null){
            this.setState({
              token: returnObject.token,
          })
          }
        })
        .then(() => {
            fetch("/api/auth/user/inventory", {method: "GET", headers:{"accepts": "application/json", "x-auth-token": this.state.token}})
            .then(res => res.json())
            .then(json => {
                console.log(json)
                for (let i = 0; i < json.inventory.length; i++){
                    //console.log(json.inventory[i].code)
                    if (json.inventory[i].code == this.props.code){
                        this.setState({
                            inventory: json.inventory[i].cards,
                        });
                    }
                }
                // this.setState({
                //     inventory: json,
                // })
                console.log("finished query")
            })
        })
    }

    renderCards = () => {
        this.setState({cardsComponent: this.state.inventory.map(card => <PackListCardComponent key={card.id} code={card.id} name={card.name} quantity={card.quantity} rarity={card.rarity} />)})
    }


    render() {

        const Styles = styled.div`
            .nav-link{
                color: #FFFFFF !important;
            }

            .navbar{
                background-color: #272727 !important;
            }
        `

        const Logo = styled.img`
            height: auto;
            width: 15%;
        `
        

        //<Logo className="float-left" src={logo}>
        //<NavbarBrand style={{color: "#FFFFFF", fontSize: "2rem",}}></NavbarBrand>
        return (
            <div>
                {/* <Modal show={this.state.displayPulledCards} onHide={this.hidePulledCards} size="lg" aria-labelledby="contained-modal-title-vcenter" centered>
                    <Modal.Header>
                        <Modal.Title id="contained-modal-title-vcenter">
                            Cards
                        </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        {this.processPulledCards()}
                    </Modal.Body>
                    <Modal.Footer>
                        <Button onClick={this.hidePulledCards}>Close</Button>
                    </Modal.Footer>
                </Modal> */}
                <Card>
                    <Card.Header>
                        <AccordionToggle as={Button} onClick={this.renderCards} variant="link" eventKey={this.props.code}>
                            {this.props.name}  
                            <Logo style={{marginLeft: "0.5rem", marginRight: "0.5rem"}}src={"https://cm4025.s3.amazonaws.com/packs/" + this.props.code + "/" + this.props.code + "Logo.jpg"}/>
                        </AccordionToggle>
                    </Card.Header>
                    <Accordion.Collapse eventKey={this.props.code}>
                        <Card.Body>
                            <Table striped bordered hover>
                                <thead>
                                    <tr>
                                        <th>Card Code</th>
                                        <th>Name</th>
                                        <th>Quantity</th>
                                        <th>Rarity</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.state.cardsComponent} 
                                </tbody>
                            </Table>
                            {this.state.inventory == null &&
                                <h1>Loading</h1>
                            }
                        </Card.Body>
                    </Accordion.Collapse>
                </Card>
            </div>
        );
    }
}
