import React from 'react';
import Button from "react-bootstrap/Button";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import NavbarBrand from "react-bootstrap/NavbarBrand";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSignOutAlt } from '@fortawesome/free-solid-svg-icons'
import { faBoxOpen } from '@fortawesome/free-solid-svg-icons'
import { faCoins } from '@fortawesome/free-solid-svg-icons'
import { faUserShield } from '@fortawesome/free-solid-svg-icons'
import { faWarehouse } from '@fortawesome/free-solid-svg-icons'
import { faPlusSquare } from '@fortawesome/free-solid-svg-icons'

import styled from "styled-components";

import logo from "../images/pokepacksonline.png"

const Styles = styled.div`
.nav-link{
    color: #FFFFFF !important;
}

.navbar{
    background-color: #272727 !important;
}

.openPacks{
    color: #FFFFFF !important
}
`

const Logo = styled.img`
height: auto;
width: 30%;
`
let Test = styled.div`
.openPacks{
    color: "white"
}
`

export default class NavbarComponent extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            ActiveRightComponent: "",
            ActiveLeftComponent: ""
          }
    }

    componentDidMount(){
         this.setState({
             ActiveLeftComponent: this.props.leftPanelComponent,
             ActiveRightComponent: this.props.rightPanelComponent
         })
    }


    handleLogOut = (event) =>{
        event.preventDefault();
        let dataToParent = {
            logOut: true
        }
        this.props.callbackToParent(dataToParent);
    }

    handleInventory = (event) =>{
        event.preventDefault();
        let dataToParent = {
            inventory: true
        }
        this.props.callbackToParent(dataToParent);
    }

    handleOpenPacks = (event) =>{
        event.preventDefault();
        let dataToParent = {
            openPacks: true
        }
        this.props.callbackToParent(dataToParent);
    }

    handleAddPacks = (event) =>{
        event.preventDefault();
        let dataToParent = {
            addPack: true
        }
        this.props.callbackToParent(dataToParent);
    }

    setActiveColor(){
        if (this.props.rightPanelComponent == "PackListComponent"){
            console.log("yes")
            Test = styled.div`
            .openPacks{
                color: "red"
            }
            `
        }
    }

    render() {
        return (
            <Styles>
                <Navbar>
                    <Logo src={logo}></Logo>

                    <Nav variant="pills" defaultActiveKey="PackListComponent" activeKey={this.props.rightPanelComponent} className="ml-auto">
                        <Nav.Link  eventKey="PackListComponent" onClick={this.handleOpenPacks}><FontAwesomeIcon icon={faBoxOpen}/> Open Packs</Nav.Link>
                        <Nav.Link eventKey="InventoryListComponent" onClick={this.handleInventory}><FontAwesomeIcon icon={faWarehouse}/> Inventory</Nav.Link>
                        <Nav.Link eventKey="AddPackComponent" onClick={this.handleAddPacks}><FontAwesomeIcon icon={faPlusSquare}/> Add New Pack From API</Nav.Link>
                        <Nav.Link eventKey="null"><FontAwesomeIcon icon={faUserShield}/> Admin</Nav.Link>
                    </Nav>
                </Navbar>
            </Styles>
            
        );
    }
}
