        const { open } = true;

        const Styles = styled.div`
            .col {
                background-color: #272727;
                width: 30rem;
                border-radius: 0;
                overflow: hidden;
                white-space: nowrap;
                text-overflow: ellipsis;
            }

            .custom-profile-image{
                width: 150px;
                height: 150px;
                border-radius: 50%;
            }
        `

        const H4 = styled.h4`
            color: #FFFFFF;
            margin-left: 2rem;
        `

        const H5 = styled.h5`
            font-size: 1.2rem;
            color: #FFFFFF;
            margin-left: 2rem;
        `

        return (
            <Styles>
                <Row>
                    <Col>
                        <Row><H4>Username: {this.state.userObject.username}</H4></Row>
                        <Row><H5>Money: ${this.state.userObject.money}</H5></Row>
                        <Row><H5>Level: {this.state.userObject.level}</H5></Row>
                        <Row><Button style={{marginLeft: "1rem", marginTop: "4rem"}} className="custom-button" variant="link" onClick={() => this.deleteAccountOnClick()}>Delete Account</Button></Row>
                    </Col>

                    <Col>
                        <Row><H5>Money Tick Rate: x{this.state.userObject.tickMultiplier}</H5></Row>
                        <Row><H5>AFK Wallet Size: {this.state.userObject.afkMax}</H5></Row>
                    </Col>
                </Row>

