import React from 'react';
import Card from "react-bootstrap/Card";
import Fade from "react-bootstrap/Fade";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSignOutAlt } from '@fortawesome/free-solid-svg-icons'
import ReactCSSTransitionGroup from 'react-addons-css-transition-group';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import styled from "styled-components";

import logo from "../images/placeholder.jpg"

import "./css/PlayerStatsComponent.css"

export default class PlayerStatsComponent extends React.Component{
    constructor(props, context) {
		super(props, context);

		this.state = {
			open: true,
		};
    }



    render() {
        const { open } = true;

        const Styles = styled.div`
            .custom-card {
                background-color: #121212;
                width: 30rem;
                height: 100vh;
                border-radius: 0;
            }

            .custom-h5{
                font-size: 1.5rem;
            }
        `

        const H5 = styled.h5`
            font-size: 1.5rem;
        `

        return (
            <Styles>
                <Card className="custom-card">
                    <Container style={{marginTop: "30px"}}>
                        <Row>
                            <Col style={{paddingLeft: "2rem", color: "#03Dac5"}}>
                                <h4 style={{}}>Player Name</h4>
                                <img width="150" height="150" src={logo} style={{borderRadius: "50%"}}/>
                            </Col>
                            <Col style={{paddingTop: "2rem", margin: "auto", color: "#03Dac5"}}>
                                <Row><H5>Money: </H5></Row>
                                <Row><p style={{fontSize: "1.5rem"}}>Level: </p></Row>
                                <p className="card">test</p>
                            </Col>
                        </Row>
                    </Container>
                </Card>
            </Styles>
        );
    }
}
