import React from 'react';
import Accordion from "react-bootstrap/Accordion";
import AccordionToggle from 'react-bootstrap/AccordionToggle';
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import Table from 'react-bootstrap/Table';
import styled from "styled-components";

const Styles = styled.div`
color: white;
`

export default class AboutComponent extends React.Component{
    constructor(props){
        super(props);

        this.state = {
          }
    }

    componentDidMount(){

    }

    

    render() {
        return (
            <Styles>
                <h2>Q: What is this website?</h2>
                <h2>A: This website is a basic pokemon card opening game! All cards are pulled from the <a href="https://pokemontcg.io/">Pokemon TCG Api</a></h2>
                <br/>
                <h2>Q: What do i do?</h2>
                <h2>A: You will start with an account at level 1. You gain money slowly over time but the rate of this can be increased with upgrades from the shop! <br/> The main idea of the game is to try and collect enough pokemon cards to move on to the next level and open more rare packs and hopefully collect em all!</h2>
                <br/>
                <h2>Q: How do i level up?</h2>
                <h2>Q: You can do this by clicking on the shop tab on the left panel and then looking at the "Level Up" shop item. <br/> There you will find a description of how many unique cards you have from each pach and how many are needed to level up!</h2>
            </Styles>
        );
    }
}
