import React from 'react';
import Button from "react-bootstrap/Button";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import NavbarBrand from "react-bootstrap/NavbarBrand";
import Accordion from "react-bootstrap/Accordion";
import Card from "react-bootstrap/Card";
import AccordionToggle from 'react-bootstrap/AccordionToggle';
import Table from 'react-bootstrap/Table';
import Modal from "react-bootstrap/Modal";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSignOutAlt } from '@fortawesome/free-solid-svg-icons'
import { faBoxOpen } from '@fortawesome/free-solid-svg-icons'
import { faCoins } from '@fortawesome/free-solid-svg-icons'

import styled from "styled-components";

import logo from "../images/pokepacksonline.png"

const Img = styled.img`
    height: auto;
    width: 30%;
`

export default class InventoryListCardComponent extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            displayCard: false,
          }
    }


    hideCard = () =>{
        this.setState({displayCard: false});
    }

    showCard = () =>{
        this.setState({displayCard: true});
    }

    processCardToDisplay = () => {
        return (
            <div style={{textAlign: "center"}}>
                <Img src={"https://cm4025.s3.eu-west-2.amazonaws.com/packs/" + this.props.packCode + "/" + this.props.code + ".jpg"}></Img>
            </div>
        );
    }

    test() {
        if (this.state.showCard == false){
            console.log("this")
            return (
            <Button onClick={this.setState({showCard: true})}>Image</Button>
            );
        }
        else {
            console.log("that")
            return (
                <Modal show={this.state.displayCard} onHide={this.hideCard} size="lg" aria-labelledby="contained-modal-title-vcenter" centered>
                <Modal.Header>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Card
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <h1>hey</h1>
                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={this.hideCard}>Close</Button>
                </Modal.Footer>
                </Modal>
                );
        }
        
    }

    handleShowImage = () => {
        let uri = "https://cm4025.s3.eu-west-2.amazonaws.com/packs/" + this.props.packCode + "/" + this.props.code + ".jpg";
        let dataForParent = {
            uri: uri
        }
        this.props.callbackToParrent(dataForParent);
    }

    render() {

        const Styles = styled.div`
            .nav-link{
                color: #FFFFFF !important;
            }

            .navbar{
                background-color: #272727 !important;
            }
        `

        const Logo = styled.img`
            height: auto;
            width: 15%;
        `

        // <Modal show={this.state.displayCard} onHide={this.hideCard} size="lg" aria-labelledby="contained-modal-title-vcenter" centered>
        // <Modal.Header>
        //     <Modal.Title id="contained-modal-title-vcenter">
        //         Card
        //     </Modal.Title>
        // </Modal.Header>
        // <Modal.Body>
        //     <h1>hey</h1>
        // </Modal.Body>
        // <Modal.Footer>
        //     <Button onClick={this.hideCard}>Close</Button>
        // </Modal.Footer>
        // </Modal>

        //<Logo className="float-left" src={logo}>
        //<NavbarBrand style={{color: "#FFFFFF", fontSize: "2rem",}}></NavbarBrand>
        return (

                <tr>
                    <td>{this.props.code}</td>
                    <td>{this.props.name}</td>
                    <td>{this.props.quantity}</td>
                    <td>{this.props.rarity}</td>
                    <td>{this.test()}</td>
                </tr>
        );
    }
}
