import {
    getFromStorage,
    setInStorage
  } from "./utils/storage"

//Component to check if the browser has a token stored we can use to log them in
function checkForLocalToken(){
    //Get token from local storage
    const token = getFromStorage("PokePacksOnline");
    //If the token exists
    if (token) {
      //Verify the token
      let headers = {
        "x-auth-token": token
      }
      fetch("api/auth/user", {method: "GET", headers: headers})
      .then(res => res.json())
      .then(json => {
        //Check if the response contains a user, if it does, then it was a valid token
        if (json._id){
          //Set state so we can display the users details
          this.setState({
            token: token,
            username: json.username,
          })
        }
      })

    } 
    //If no token was found
    else {
      console.log("no token");
      this.setState({
        token: "",
      })
    }
  }

  exports.ticker = ticker();
