import React from 'react';
import Button from "react-bootstrap/Button";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import NavbarBrand from "react-bootstrap/NavbarBrand";
import Accordion from "react-bootstrap/Accordion";
import Card from "react-bootstrap/Card";
import AccordionToggle from 'react-bootstrap/AccordionToggle';
import Table from 'react-bootstrap/Table';
import Modal from "react-bootstrap/Modal";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSignOutAlt } from '@fortawesome/free-solid-svg-icons'
import { faBoxOpen } from '@fortawesome/free-solid-svg-icons'
import { faCoins } from '@fortawesome/free-solid-svg-icons'

import styled from "styled-components";

import logo from "../images/pokepacksonline.png"

const Img = styled.img`
    height: auto;
    width: 30%;
`

export default class InventoryListCardComponent extends React.Component{
    // consturctor(props){
    //     super(props);
    // }


    hideCard = () =>{
        this.setState({displayCard: false});
    }

    showCard = () =>{
        this.setState({displayCard: true});
    }

    processCardToDisplay = () => {
        // let test = "";
        // for (let i = 0; i < this.state.pulledCards.length; i++){
        //     test += <p><script>alert("hello");</script></p>;
        // }
        if (this.state.pulledCards.error){
            return (
                <div style={{textAlign: "center"}}>
                    <h1>Sorry Still Processing Last Pack, Please Try Again In A Few Seconds</h1>
                </div>
            );
        } else {
            if (this.state.pulledCards[0] && this.state.pulledCards[1] && this.state.pulledCards[2]){
                return (
                    <div style={{textAlign: "center"}}>
                        <Img src={"https://cm4025.s3.eu-west-2.amazonaws.com/packs/" + this.state.pulledCards[0].setCode + "/" + this.state.pulledCards[0].id + ".jpg"}></Img>
                        <Img src={"https://cm4025.s3.eu-west-2.amazonaws.com/packs/" + this.state.pulledCards[1].setCode + "/" + this.state.pulledCards[1].id + ".jpg"}></Img>
                        <Img src={"https://cm4025.s3.eu-west-2.amazonaws.com/packs/" + this.state.pulledCards[2].setCode + "/" + this.state.pulledCards[2].id + ".jpg"}></Img>
                    </div>
                );
            } else {
                return;
            }
        }

    }

    render() {

        const Styles = styled.div`
            .nav-link{
                color: #FFFFFF !important;
            }

            .navbar{
                background-color: #272727 !important;
            }
        `

        const Logo = styled.img`
            height: auto;
            width: 15%;
        `


        //<Logo className="float-left" src={logo}>
        //<NavbarBrand style={{color: "#FFFFFF", fontSize: "2rem",}}></NavbarBrand>
        return (
            <div>
                <Modal show={this.state.displayCard} onHide={this.hideCard} size="lg" aria-labelledby="contained-modal-title-vcenter" centered>
                <Modal.Header>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Card
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {this.processPulledCards()}
                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={this.hidePulledCards}>Close</Button>
                </Modal.Footer>
                </Modal>
                <tr>
                    <td>{this.props.code}</td>
                    <td>{this.props.name}</td>
                    <td>{this.props.quantity}</td>
                    <td>{this.props.rarity}</td>
                </tr>
            </div>
        );
    }
}
