import React from 'react';
import Button from "react-bootstrap/Button";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import NavbarBrand from "react-bootstrap/NavbarBrand";
import Accordion from "react-bootstrap/Accordion";
import Card from "react-bootstrap/Card";
import AccordionToggle from 'react-bootstrap/AccordionToggle';
import Table from 'react-bootstrap/Table';
import Modal from "react-bootstrap/Modal";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSignOutAlt } from '@fortawesome/free-solid-svg-icons'
import { faBoxOpen } from '@fortawesome/free-solid-svg-icons'
import { faCoins } from '@fortawesome/free-solid-svg-icons'

import styled from "styled-components";

import logo from "../images/pokepacksonline.png"

export default class InventoryListCardComponent extends React.Component{
    // consturctor(props){
    //     super(props);
    // }


    render() {

        const Styles = styled.div`
            .nav-link{
                color: #FFFFFF !important;
            }

            .navbar{
                background-color: #272727 !important;
            }
        `

        const Logo = styled.img`
            height: auto;
            width: 15%;
        `


        //<Logo className="float-left" src={logo}>
        //<NavbarBrand style={{color: "#FFFFFF", fontSize: "2rem",}}></NavbarBrand>
        return (
            <div>
                <Modal show={this.state.displayCard} onHide={this.hideCard} size="lg" aria-labelledby="contained-modal-title-vcenter" centered>
                <Modal.Header>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Card
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {this.processPulledCards()}
                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={this.hidePulledCards}>Close</Button>
                </Modal.Footer>
                </Modal>
                <tr>
                    <td>{this.props.code}</td>
                    <td>{this.props.name}</td>
                    <td>{this.props.quantity}</td>
                    <td>{this.props.rarity}</td>
                </tr>
            </div>
        );
    }
}
