import React from 'react';
import Button from "react-bootstrap/Button";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import NavbarBrand from "react-bootstrap/NavbarBrand";
import Accordion from "react-bootstrap/Accordion";
import Modal from "react-bootstrap/Modal";
import Card from "react-bootstrap/Card";
import AccordionToggle from 'react-bootstrap/AccordionToggle';
import Table from 'react-bootstrap/Table';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSignOutAlt } from '@fortawesome/free-solid-svg-icons'
import { faBoxOpen } from '@fortawesome/free-solid-svg-icons'
import { faCoins } from '@fortawesome/free-solid-svg-icons'

import styled from "styled-components";

import logo from "../images/pokepacksonline.png"

import PackListCardComponent from "./PackListCardComponent";

const fetch = require("node-fetch");
const checkForLocalToken = require("../utils/checkForLocalToken");


const Styles = styled.div`
.nav-link{
    color: #FFFFFF !important;
}

.navbar{
    background-color: #272727 !important;
}
`

const Logo = styled.img`
height: auto;
width: 15%;
`

const Img = styled.img`
    height: auto;
    width: 30%;
`

export default class PackListComponent extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            rarities: [],
            packProperties: null,
        }
    }

    componentDidMount(){
        let rarities = this.state.rarities
        for(var key in this.props.rarities){
            if (this.props.rarities.hasOwnProperty(key)){
                rarities.push([key, this.props.rarities[key]])
            }
        }

        this.setState({
            rarities: rarities
        })
    }



    renderProperties = () => {
        console.log("ran")
        this.setState({
            packProperties: this.state.rarities.map(rarity => <tr><td>{rarity[0]}</td><td><Form.Control type="string" placeholder=""/></td></tr>)
        })
        //return this.state.rarityNames.map(card => <tr>hello</tr>)
        
    }


   
    render() {
        

        //<Logo className="float-left" src={logo}>
        //<NavbarBrand style={{color: "#FFFFFF", fontSize: "2rem",}}></NavbarBrand>
        return (
            <div>
                <Card>
                    <Card.Header>
                        <AccordionToggle as={Button} onClick={this.renderProperties} variant="link" eventKey={this.props.code+"Server"}>
                            {this.props.name}  
                            <Logo style={{marginLeft: "0.5rem", marginRight: "0.5rem"}}src={"https://cm4025.s3.amazonaws.com/packs/" + this.props.code + "/" + this.props.code + "Logo.jpg"}/>
                        </AccordionToggle>
                        <br></br>
                    </Card.Header>
                    <Accordion.Collapse eventKey={this.props.code+"Server"}>
                        <Card.Body>
                            <Table striped bordered hover>
                                <thead>
                                    <tr>
                                        <th>Property Name</th>
                                        <th>Value</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.state.packProperties} 
                                    {this.state.packProperties2} 
                                </tbody>
                            </Table>
                            <Button>Submit</Button>
                        </Card.Body>
                    </Accordion.Collapse>
                </Card>
            </div>
        );
    }
}
