import React from 'react';
import Card from "react-bootstrap/Card";
import Fade from "react-bootstrap/Fade";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSignOutAlt } from '@fortawesome/free-solid-svg-icons'
import ReactCSSTransitionGroup from 'react-addons-css-transition-group';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import styled from "styled-components";

import logo from "../images/placeholder.jpg"


export default class PlayerStatsComponent extends React.Component{
    constructor(props, context) {
		super(props, context);

		this.state = {
            seconds: 0,
            open: true,
		};
    }

    tick() {
        this.setState(prevState => ({
          seconds: prevState.seconds + 1
        }));

        
      }

    componentDidMount(){
        
        this.interval = setInterval(() => this.tick(), 1000);
    }

    
  componentWillUnmount(){
    clearInterval(this.interval);
  }

    render() {
        const { open } = true;

        const Styles = styled.div`
            .col {
                background-color: #272727;
                width: 30rem;
                height: 100vh;
                border-radius: 0;
            }

            .custom-profile-image{
                width: 150px;
                height: 150px;
                border-radius: 50%;
            }
        `

        const H4 = styled.h4`
            color: #FFFFFF;
        `

        const H5 = styled.h5`
            font-size: 1.2rem;
            color: #FFFFFF;
        `

        return (
            <Styles>
                <Row>
                    <Col>
                        <H4>{this.props.username}</H4>
                        <img className="custom-profile-image" src={logo}/>
                    </Col>
                    <Col>
                        <Row><H5>Money: ${this.props.money}</H5></Row>
                        <Row><H5>Level: {this.props.level}</H5></Row>
                    </Col>
                </Row>

            </Styles>
        );
    }
}
