import React from 'react';
import Button from "react-bootstrap/Button";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import NavbarBrand from "react-bootstrap/NavbarBrand";
import Accordion from "react-bootstrap/Accordion";
import Modal from "react-bootstrap/Modal";
import Card from "react-bootstrap/Card";
import AccordionToggle from 'react-bootstrap/AccordionToggle';
import Table from 'react-bootstrap/Table';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSignOutAlt } from '@fortawesome/free-solid-svg-icons'
import { faBoxOpen } from '@fortawesome/free-solid-svg-icons'
import { faCoins } from '@fortawesome/free-solid-svg-icons'

import styled from "styled-components";

import logo from "../images/pokepacksonline.png"

const fetch = require("node-fetch");
const checkForLocalToken = require("../utils/checkForLocalToken");

const Styles = styled.div`

`

const Img = styled.img`
    height: auto;
    width: 30%;
`

export default class ShopItemListComponent extends React.Component{
    constructor(props){
        super(props);

        this.state = {

          }
    }

    componentDidMount(){

    }


    render() {

        const Styles = styled.div`
            .nav-link{
                color: #FFFFFF !important;
            }

            .navbar{
                background-color: #272727 !important;
            }
        `
        
        return (
            <div>
                <Card>
                    <Card.Header>
                        <AccordionToggle as={Button} onClick={this.renderCards} variant="link" eventKey={this.props.code+"User"}>
                            {this.props.name}  
                            <Logo style={{marginLeft: "0.5rem", marginRight: "0.5rem"}}src={"https://cm4025.s3.amazonaws.com/packs/" + this.props.code + "/" + this.props.code + "Logo.jpg"}/>
                        </AccordionToggle>
                    </Card.Header>
                    <Accordion.Collapse eventKey={this.props.code+"User"}>
                        <Card.Body>
                            <Table striped bordered hover>
                                <thead>
                                    <tr>
                                        <th>Card Code</th>
                                        <th>Name</th>
                                        <th>Quantity</th>
                                        <th>Rarity</th>
                                        <th>Image</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.state.cardsComponent} 
                                </tbody>
                            </Table>
                            <h1>{this.state.informationMessage}</h1>
                        </Card.Body>
                    </Accordion.Collapse>
                </Card>
            </div>
        );
    }
}
