import React from 'react';
import {BrowserRouter as Router, Route} from "react-router-dom";
import './App.css';
import { render } from '@testing-library/react';
import ReactCSSTransitionGroup from 'react-addons-css-transition-group';
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import Accordion from "react-bootstrap/Accordion";
import Card from "react-bootstrap/Card";
import AccordionToggle from 'react-bootstrap/AccordionToggle';
import Button from 'react-bootstrap/Button';
import styled from "styled-components";

import {
  getFromStorage,
  setInStorage
} from "./utils/storage"

//Component Imports
import NavbarComponent from './components/NavbarComponent';
import PlayerStatsComponent from './components/PlayerStatsComponent';
import LoginComponent from './components/LoginComponent';
import RegisterComponent from './components/RegisterComponent';
import PacksCarouselComponent from "./components/PacksCarouselComponent";
import PackListComponent from "./components/PackListComponent";



const fetch = require("node-fetch");

class App extends React.Component {
  constructor(props){
    super(props);

    this.state = {
      time: Date.now(),
      packs: [],
      token: "",
      username: "",
      email: "",
      money: 0,
      level: 0,
      leftPanelComponent: "PlayerStatsComponent",
    }
  }

  //Component to check if the browser has a token stored we can use to log them in
  checkForLocalToken(){
    //Get token from local storage
    const token = getFromStorage("PokePacksOnline");
    //If the token exists
    if (token) {
      //Verify the token
      let headers = {
        "x-auth-token": token
      }
      fetch("api/auth/user", {method: "GET", headers: headers})
      .then(res => res.json())
      .then(json => {
        //Check if the response contains a user, if it does, then it was a valid token
        if (json._id){
          //Set state so we can display the users details
          this.setState({
            token: token,
            username: json.username,
            email: json.email,
            money: json.money,
            level: json.level,
          })
        }
      })

    } 
    //If no token was found
    else {
      console.log("no token");
      this.setState({
        token: "",
      })
    }
  }

  componentDidMount(){
    //Quick check to see if the user has an existing token
    this.checkForLocalToken();

    //Fetch all the packs that exist in the database to be displayed
    fetch("/api/packs", {method: "GET", headers:{"accepts": "application/json"}})
      .then(res => res.json())
      .then(json => {
        for (let i = 0; i < json.packs.length; i++){
          let tempArray = this.state.packs
          tempArray.push(json.packs[i]);
          this.setState({packs: tempArray});
        }
      })

      if (this.state.componentToLoad === "PlayerStatsComponent"){
        console.log("ran");

      }
  }

  componentWillUnmount(){
    clearInterval(this.interval);
  }

  //Function that is used to change what renders on the left panel
  leftPanelComponentToRender(){
    switch(this.state.leftPanelComponent){
      case "LoginComponent":
        return <LoginComponent callbackToParent={this.leftPanelComponentChildCallback}/>
      case "RegisterComponent":
        return <RegisterComponent callbackToParent={this.leftPanelComponentChildCallback}/>
      case "PlayerStatsComponent":
        if(this.state.token){
          return <PlayerStatsComponent username={this.state.username} money={this.state.money} level={this.state.level} callbackToParent={this.leftPanelComponentChildCallback}/>  
        } else {
          return <LoginComponent callbackToParent={this.leftPanelComponentChildCallback}/>
        }
    }
  }

  //The callback all components that use the left panel use to communicate with the parent (this)
  leftPanelComponentChildCallback = dataFromChild => {
    //if the child is telling the parent to load a new component
    if (dataFromChild.componentToLoad){
      this.setState({
        leftPanelComponent: dataFromChild.componentToLoad,
      })
    } 
    //If the child wants to log a user in
    else if(dataFromChild.login) {
      fetch("/api/auth/", {method: "POST", headers:{"accepts": "application/json", "Content-Type": "application/json"}, body: JSON.stringify({"email": dataFromChild.email, "password": dataFromChild.password})})
      .then(res => res.json())
      .then(json => {
        if (json.token){
           setInStorage("PokePacksOnline", json.token);
           this.setState({
            leftPanelComponent: "PlayerStatsComponent",
          })
           this.checkForLocalToken();
        } else {
          console.log("Invalid credentials");
        }
      })
    } 
    //If the child wants to register a user
    else if (dataFromChild.register){
      fetch("/api/users/", {method: "POST", headers:{"accepts": "application/json", "Content-Type": "application/json"}, body: JSON.stringify({"username": dataFromChild.username, "email": dataFromChild.email, "password": dataFromChild.password})})
      .then(res => res.json())
      .then(json => {
        if (json.token){
           this.setState({
             leftPanelComponent: "LoginComponent",
           })
        } else {
          console.log("Invalid credentials");
        }
      })
    }

  }

  //Renders all the packs to the main screen
  renderPacks(packs) {
    return packs.map(pack => <PackListComponent key={pack.code} code={pack.code} name={pack.name} cards={pack.cards} />)
  }
  render() {
    const Styles = styled.div`
    .col-sm-4 {
        padding-left: 0 !important;
    }
    `

    const {packs} = this.state;
    return (
      <div className="App" style={{backgroundColor: "#121212"}}>
        <Styles>
          <NavbarComponent/>
          <Row style={{margin: "0 auto"}}>
            <Col sm={4}>
              {this.leftPanelComponentToRender()}
            </Col>
            <Col style={{marginTop: "5rem"}} sm={8}>
              <Accordion>
                {this.renderPacks(this.state.packs)}
              </Accordion>
            </Col>
          </Row>
        </Styles>

      </div>
    );
  }
}

export default App;
