import React from 'react';
import Button from "react-bootstrap/Button";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import NavbarBrand from "react-bootstrap/NavbarBrand";
import Accordion from "react-bootstrap/Accordion";
import Modal from "react-bootstrap/Modal";
import Card from "react-bootstrap/Card";
import AccordionToggle from 'react-bootstrap/AccordionToggle';
import Table from 'react-bootstrap/Table';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSignOutAlt } from '@fortawesome/free-solid-svg-icons'
import { faBoxOpen } from '@fortawesome/free-solid-svg-icons'
import { faCoins } from '@fortawesome/free-solid-svg-icons'

import styled from "styled-components";

import logo from "../images/pokepacksonline.png"

import InventoryListCardComponent from "./InventoryListCardComponent";

const fetch = require("node-fetch");
const checkForLocalToken = require("../utils/checkForLocalToken");

const Styles = styled.div`

`

const Img = styled.img`
    height: auto;
    width: 30%;
`

export default class InventoryListComponent extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            inventory: null,
            cardsComponent: null,
            informationMessage: "Loading...",
            displayCard: false,
          }
    }

    componentDidMount(){
        
    }

    renderCards = () => {
        checkForLocalToken.checkForLocalTokenNoUserObject()
        .then(returnObject => {
          if (returnObject != null){
            this.setState({
              token: returnObject.token,
          })
          }
        })
        .then(async () => {
            await fetch("/api/auth/user/inventory", {method: "GET", headers:{"accepts": "application/json", "x-auth-token": this.state.token}})
            .then(res => res.json())
            .then(json => {
                console.log(json)
                for (let i = 0; i < json.inventory.length; i++){
                    //console.log(json.inventory[i].code)
                    if (json.inventory[i].code == this.props.code){
                        this.setState({
                            inventory: json.inventory[i].cards,
                        });
                    }
                }
            })
        })
        .then(() => {
            if (this.state.inventory == null){
                this.setState({informationMessage: "You Own No Cards From This Set"})
            } else {
                this.setState({cardsComponent: this.state.inventory.map(card => <InventoryListCardComponent key={card.id} code={card.id} name={card.name} quantity={card.quantity} rarity={card.rarity} />)})
                this.setState({informationMessage: null})
            }
            
        })
        
    }

    hideCard = () =>{
        this.setState({displayCard: false});
    }

    showCard = () =>{
        this.setState({displayCard: true});
    }

    processCardToDisplay = () => {
        // let test = "";
        // for (let i = 0; i < this.state.pulledCards.length; i++){
        //     test += <p><script>alert("hello");</script></p>;
        // }
        if (this.state.pulledCards.error){
            return (
                <div style={{textAlign: "center"}}>
                    <h1>Sorry Still Processing Last Pack, Please Try Again In A Few Seconds</h1>
                </div>
            );
        } else {
            if (this.state.pulledCards[0] && this.state.pulledCards[1] && this.state.pulledCards[2]){
                return (
                    <div style={{textAlign: "center"}}>
                        <Img src={"https://cm4025.s3.eu-west-2.amazonaws.com/packs/" + this.state.pulledCards[0].setCode + "/" + this.state.pulledCards[0].id + ".jpg"}></Img>
                        <Img src={"https://cm4025.s3.eu-west-2.amazonaws.com/packs/" + this.state.pulledCards[1].setCode + "/" + this.state.pulledCards[1].id + ".jpg"}></Img>
                        <Img src={"https://cm4025.s3.eu-west-2.amazonaws.com/packs/" + this.state.pulledCards[2].setCode + "/" + this.state.pulledCards[2].id + ".jpg"}></Img>
                    </div>
                );
            } else {
                return;
            }
        }

    }

    render() {

        const Styles = styled.div`
            .nav-link{
                color: #FFFFFF !important;
            }

            .navbar{
                background-color: #272727 !important;
            }
        `

        const Logo = styled.img`
            height: auto;
            width: 15%;
        `
        

        //<Logo className="float-left" src={logo}>
        //<NavbarBrand style={{color: "#FFFFFF", fontSize: "2rem",}}></NavbarBrand>
        return (
            <div>
                <Card>
                    <Card.Header>
                        <AccordionToggle as={Button} onClick={this.renderCards} variant="link" eventKey={this.props.code}>
                            {this.props.name}  
                            <Logo style={{marginLeft: "0.5rem", marginRight: "0.5rem"}}src={"https://cm4025.s3.amazonaws.com/packs/" + this.props.code + "/" + this.props.code + "Logo.jpg"}/>
                        </AccordionToggle>
                    </Card.Header>
                    <Accordion.Collapse eventKey={this.props.code}>
                        <Card.Body>
                            <Table striped bordered hover>
                                <thead>
                                    <tr>
                                        <th>Card Code</th>
                                        <th>Name</th>
                                        <th>Quantity</th>
                                        <th>Rarity</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.state.cardsComponent} 
                                </tbody>
                            </Table>
                            <h1>{this.state.informationMessage}</h1>
                        </Card.Body>
                    </Accordion.Collapse>
                </Card>
            </div>
        );
    }
}
