import React from 'react';
import Button from "react-bootstrap/Button";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import NavbarBrand from "react-bootstrap/NavbarBrand";
import Accordion from "react-bootstrap/Accordion";
import Modal from "react-bootstrap/Modal";
import Card from "react-bootstrap/Card";
import AccordionToggle from 'react-bootstrap/AccordionToggle';
import Table from 'react-bootstrap/Table';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSignOutAlt } from '@fortawesome/free-solid-svg-icons'
import { faBoxOpen } from '@fortawesome/free-solid-svg-icons'
import { faCoins } from '@fortawesome/free-solid-svg-icons'

import styled from "styled-components";

import logo from "../images/pokepacksonline.png"

import CardListComponent from "./CardListComponent";

const fetch = require("node-fetch");
const checkForLocalToken = require("../utils/checkForLocalToken");

const Styles = styled.div`

`

const Img = styled.img`
    height: auto;
    width: 30%;
`

export default class PackListComponent extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            cost: 0,
            displayPulledCards: false,
            pulledCards: {},
            cardsComponent: null,
          }
    }

    componentDidMount(){

    }

    renderCards = () => {

        checkForLocalToken.checkForLocalTokenNoUserObject()
        .then(returnObject => {
          if (returnObject != null){
            this.setState({
              token: returnObject.token,
          })
          }
        })
        .then(() => {
            fetch("/api/users/user/inventory", {method: "GET", headers:{"accepts": "application/json", "x-auth-token": this.state.token}})
            .then(res => res.json())
            .then(json => {
                console.log(json)
            })
        })

        // fetch("/api/packs/" + this.props.code, {method: "GET", headers:{"accepts": "application/json"}})
        // .then(res => res.json())
        // .then(json => {
        //     // this.setState({
        //     //     cardsComponent: json.cards.map(card => <CardListComponent key={card.id} code={card.id} name={card.name} rarity={card.rarity} />)
        //     // })
        // })

        //return cards.map(card => <CardListComponent key={card.id} code={card.id} name={card.name} rarity={card.rarity} />)
    }

    openPack = (event) => {
        event.preventDefault();
        checkForLocalToken.checkForLocalTokenNoUserObject()
        .then(returnObject => {
            if (returnObject != null){
                fetch("/api/packs/open/"+this.props.code, {method: "GET", headers:{"accepts": "application/json", "x-auth-token": returnObject.token}})
                .then(res => res.json())
                .then(json => {
                    this.setState({
                        pulledCards: json,
                    })
                    this.showPulledCards();
                })

                
            }
        })
    }

    hidePulledCards = () =>{
        this.setState({displayPulledCards: false});
    }

    showPulledCards = () =>{
        this.setState({displayPulledCards: true});
    }

    processPulledCards = () => {
        // let test = "";
        // for (let i = 0; i < this.state.pulledCards.length; i++){
        //     test += <p><script>alert("hello");</script></p>;
        // }
        if (this.state.pulledCards.error){
            return (
                <div style={{textAlign: "center"}}>
                    <h1>Sorry Still Processing Last Pack, Please Try Again In A Few Seconds</h1>
                </div>
            );
        } else {
            if (this.state.pulledCards[0] && this.state.pulledCards[1] && this.state.pulledCards[2]){
                return (
                    <div style={{textAlign: "center"}}>
                        <Img src={"https://cm4025.s3.eu-west-2.amazonaws.com/packs/" + this.state.pulledCards[0].setCode + "/" + this.state.pulledCards[0].id + ".jpg"}></Img>
                        <Img src={"https://cm4025.s3.eu-west-2.amazonaws.com/packs/" + this.state.pulledCards[1].setCode + "/" + this.state.pulledCards[1].id + ".jpg"}></Img>
                        <Img src={"https://cm4025.s3.eu-west-2.amazonaws.com/packs/" + this.state.pulledCards[2].setCode + "/" + this.state.pulledCards[2].id + ".jpg"}></Img>
                    </div>
                );
            } else {
                return;
            }
        }

    }

    test = () => {
        console.log("hello");
    }
    

    render() {

        const Styles = styled.div`
            .nav-link{
                color: #FFFFFF !important;
            }

            .navbar{
                background-color: #272727 !important;
            }
        `

        const Logo = styled.img`
            height: auto;
            width: 15%;
        `
        

        //<Logo className="float-left" src={logo}>
        //<NavbarBrand style={{color: "#FFFFFF", fontSize: "2rem",}}></NavbarBrand>
        return (
            <div>
                {/* <Modal show={this.state.displayPulledCards} onHide={this.hidePulledCards} size="lg" aria-labelledby="contained-modal-title-vcenter" centered>
                    <Modal.Header>
                        <Modal.Title id="contained-modal-title-vcenter">
                            Cards
                        </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        {this.processPulledCards()}
                    </Modal.Body>
                    <Modal.Footer>
                        <Button onClick={this.hidePulledCards}>Close</Button>
                    </Modal.Footer>
                </Modal> */}
                <Card>
                    <Card.Header>
                        <AccordionToggle as={Button} onClick={this.renderCards} variant="link" eventKey={this.props.code}>
                            {this.props.name}  
                            <Logo style={{marginLeft: "0.5rem", marginRight: "0.5rem"}}src={"https://cm4025.s3.amazonaws.com/packs/" + this.props.code + "/" + this.props.code + "Logo.jpg"}/>
                        </AccordionToggle>
                        <br></br>
                        <Button variant="success" onClick={this.openPack}>Open Pack ${this.state.cost}</Button>
                    </Card.Header>
                    <Accordion.Collapse eventKey={this.props.code}>
                        <Card.Body>
                            <Table striped bordered hover>
                                <thead>
                                    <tr>
                                        <th>Card Code</th>
                                        <th>Name</th>
                                        <th>Rarity</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.state.cardsComponent} 
                                </tbody>
                            </Table>
                            {this.state.cardsComponent == null &&
                                <h1>Loading</h1>
                            }
                        </Card.Body>
                    </Accordion.Collapse>
                </Card>
            </div>
        );
    }
}
