import React from 'react';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import styled from "styled-components";

const checkForLocalToken = require("../utils/checkForLocalToken");

export default class PlayerStatsComponent extends React.Component{
    constructor(props, context) {
		super(props, context);

		this.state = {
            seconds: 0,
            userObject: {},
            open: true,
		};
    }

    tick() {
        checkForLocalToken.checkForLocalToken()
        .then(returnObject => {
          if (returnObject != null){
            this.setState({
              userObject: returnObject.userObject,
          })
          }
        })

        this.setState(prevState => ({
          seconds: prevState.seconds + 1
        }));


      }

    componentDidMount(){
        this.tick()
        this.interval = setInterval(() => this.tick(), 5000);
    }

    
  componentWillUnmount(){
    clearInterval(this.interval);
  }

    render() {
        const { open } = true;

        const Styles = styled.div`
            .col {
                background-color: #272727;
                width: 30rem;
                border-radius: 0;
                overflow: hidden;
                white-space: nowrap;
                text-overflow: ellipsis;
            }

            .custom-profile-image{
                width: 150px;
                height: 150px;
                border-radius: 50%;
            }
        `

        const H4 = styled.h4`
            color: #FFFFFF;
            margin-left: 2rem;
        `

        const H5 = styled.h5`
            font-size: 1.2rem;
            color: #FFFFFF;
            margin-left: 2rem;
        `

        return (
            <Styles>
                <Row>
                    <Col>
                        <Row><H4>Username: {this.state.userObject.username}</H4></Row>
                        <Row><H5>Money: ${this.state.userObject.money}</H5></Row>
                        <Row><H5>Level: {this.state.userObject.level}</H5></Row>
                        <Row><Button style={{marginLeft: "1rem"}} className="custom-button" variant="link" onClick={() => this.loginOnClick()}>Delete Account</Button></Row>
                    </Col>

                    <Col>
                        <Row><H5>Money Tick Rate: x{this.state.userObject.tickMultiplier}</H5></Row>
                        <Row><H5>AFK Wallet Size: {this.state.userObject.afkMax}</H5></Row>
                    </Col>
                </Row>

            </Styles>
        );
    }
}
