import React from 'react';
import Card from "react-bootstrap/Card";
import Fade from "react-bootstrap/Fade";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSignOutAlt } from '@fortawesome/free-solid-svg-icons'
import ReactCSSTransitionGroup from 'react-addons-css-transition-group';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import styled from "styled-components";
import AccordionToggle from 'react-bootstrap/AccordionToggle';
import Table from 'react-bootstrap/Table';
import Accordion from "react-bootstrap/Accordion";
import Button from "react-bootstrap/Button";

import ShopItemListComponent from "./ShopItemListComponent";

const checkForLocalToken = require("../utils/checkForLocalToken");

const Styles = styled.div`
            .col {
                background-color: #272727;
                width: 30rem;
                height: 100vh;
                border-radius: 0;
            }

            .custom-profile-image{
                width: 150px;
                height: 150px;
                border-radius: 50%;
            }
        `

        const H4 = styled.h4`
            color: #FFFFFF;
        `

        const H5 = styled.h5`
            font-size: 1.2rem;
            color: #FFFFFF;
        `

export default class ShopComponent extends React.Component{
    constructor(props, context) {
		super(props, context);

		this.state = {

		};
    }


    componentDidMount(){

    }

    renderLevelUp = () => {
        return (

            <Card>
                    <Card.Header>
                        <AccordionToggle as={Button} onClick={this.renderItem} variant="link" eventKey={"test"}>
                            Level Up
                        </AccordionToggle>
                    </Card.Header>
                    <Accordion.Collapse >
                        <Card.Body>
                            <Table striped bordered hover>
                                <thead>
                                    <tr>
                                        <th>Item Name</th>
                                        <th>Description</th>
                                        <th>Cost</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.state.itemsComponents} 
                                </tbody>
                            </Table>
                            <h1>{this.state.informationMessage}</h1>
                        </Card.Body>
                    </Accordion.Collapse>
                </Card>
        )
    }


    render() {
        

        return (
            <Styles>
                <Col>
                    {this.renderLevelUp()}
                    <ShopItemListComponent checkDatabase={false}/>
                </Col>
            </Styles>
        );
    }
}
