import React from 'react';
import Card from "react-bootstrap/Card";
import Fade from "react-bootstrap/Fade";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSignOutAlt } from '@fortawesome/free-solid-svg-icons'
import ReactCSSTransitionGroup from 'react-addons-css-transition-group';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import logo from "../images/placeholder.jpg"

export default class PlayerStatsComponent extends React.Component{
    constructor(props, context) {
		super(props, context);

		this.state = {
			open: true,
		};
    }

    test(){
        console.log("hello");
    }
    
    render() {
        const { open } = true;
        return (
            <Card style={{width: "30rem", height: "100vh", backgroundColor: "#121212", borderRadius: 0}}>
                <Container style={{marginTop: "30px"}}>
                    <Row>
                        <Col style={{paddingLeft: "2rem"}}>
                            <Row style={{width: "auto"}}><h4 style={{color: "#BB86FC"}}>Player Name</h4></Row>
                            <Row><img width="150" height="150" src={logo} style={{borderRadius: "50%"}}/></Row>
                        </Col>
                        <Col style={{paddingTop: "2rem", margin: "auto"}}>
                            <Row><h5>Money: </h5></Row>
                            <Row><p style={{fontSize: "1.5rem"}}>Level: </p></Row>
                        </Col>
                    </Row>
                </Container>
            </Card>
        );
    }
}
