import React from 'react';
import {BrowserRouter as Router, Route} from "react-router-dom";
import './App.css';
import { render } from '@testing-library/react';
import ReactCSSTransitionGroup from 'react-addons-css-transition-group';
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import Accordion from "react-bootstrap/Accordion";
import Card from "react-bootstrap/Card";
import AccordionToggle from 'react-bootstrap/AccordionToggle';
import Button from 'react-bootstrap/Button';
import styled from "styled-components";

//Component Imports
import NavbarComponent from './components/NavbarComponent';
import PlayerStatsComponent from './components/PlayerStatsComponent';
import LoginComponent from './components/LoginComponent';
import PacksCarouselComponent from "./components/PacksCarouselComponent";
import PackListComponent from "./components/PackListComponent";



const fetch = require("node-fetch");

class App extends React.Component {
  constructor(props){
    super(props);

    this.state = {
      packs: [],
      loggedIn: false,
    }
  }

  componentDidMount(){
    fetch("http://localhost:5000/packs/")
      .then(res => res.json())
      .then(json => {
        for (let i = 0; i < json.packs.length; i++){
          let tempArray = this.state.packs
          tempArray.push(json.packs[i]);
          this.setState({packs: tempArray});
        }
      })
  }

  renderPacks(packs) {
    return packs.map(pack => <PackListComponent key={pack.code} code={pack.code} name={pack.name} cards={pack.cards} />)
  }
  render() {
    const Styles = styled.div`
    .col {
        padding-left: 0 !important;
    }
    `

    const {packs} = this.state;
    return (
      <div className="App" style={{backgroundColor: "#121212"}}>
        <Styles>
          <NavbarComponent/>
          <Row style={{margin: "0 auto"}}>
            <Col sm={4}>
              {this.state.loggedIn ? <PlayerStatsComponent/> : <LoginComponent/>}
            </Col>
            <Col style={{marginTop: "5rem"}} sm={8}>
              <Accordion>
                {this.renderPacks(this.state.packs)}
              </Accordion>
            </Col>
          </Row>
        </Styles>

      </div>
    );
  }
}

export default App;
